import osfrom flask import Flask, request, render_templateapp = Flask(__name__)app.config['ALLOWED_EXTENSIONS'] = set(['c', 'cpp'])def allowed_file(filename):    return '.' in filename and \            filename.rsplit('.', 1)[1].lower() in app.config['ALLOWED_EXTENSIONS']APP_ROOT = os.path.dirname(os.path.abspath(__file__))@app.route('/')def index():    return render_template("Inputcode.html")@app.route('/upload', methods=['POST', 'GET'])def upload():    target = os.path.join(APP_ROOT,'codes/')    print(target)    if not os.path.isdir(target):        os.mkdir(target)    for file in request.files.getlist(" Code "):            print(file)            filename = file.filename            destination = "/".join([target,filename])            print(destination)            file.save(destination)            return render_template("Outputcode.html")if __name__=="__main__":    app.run(port=4555, debug = True)